// Core module
var core = require('mongodb-core'),
  Instrumentation = require('mongodb/lib/apm');

// Set up the connect function
var connect = require('mongodb/lib/mongo_client').connect;

// Expose error class
connect.MongoError = core.MongoError;

// Actual driver classes exported
connect.Admin = require('mongodb/lib/admin');
connect.MongoClient = require('mongodb/lib/mongo_client');
connect.Db = require('mongodb/lib/db');
connect.Collection = require('mongodb/lib/collection');
connect.Server = require('mongodb/lib/server');
connect.ReplSet = require('mongodb/lib/replset');
connect.Mongos = require('mongodb/lib/mongos');
connect.ReadPreference = require('mongodb/lib/read_preference');
connect.GridStore = require('mongodb/lib/gridfs/grid_store');
connect.Chunk = require('mongodb/lib/gridfs/chunk');
connect.Logger = core.Logger;
connect.Cursor = require('mongodb/lib/cursor');
connect.GridFSBucket = require('mongodb/lib/gridfs-stream');

// BSON types exported
connect.Binary = core.BSON.Binary;
connect.Code = core.BSON.Code;
connect.Map = core.BSON.Map;
connect.DBRef = core.BSON.DBRef;
connect.Double = core.BSON.Double;
connect.Long = core.BSON.Long;
connect.MinKey = core.BSON.MinKey;
connect.MaxKey = core.BSON.MaxKey;
connect.ObjectID = core.BSON.ObjectID;
connect.ObjectId = core.BSON.ObjectID;
connect.Symbol = core.BSON.Symbol;
connect.Timestamp = core.BSON.Timestamp;

// Add connect method
connect.connect = connect;

// Set up the instrumentation method
connect.instrument = function(options, callback) {
  if(typeof options == 'function') callback = options, options = {};
  return new Instrumentation(core, options, callback);
}

// Set our exports to be the connect function
module.exports = connect;
